  @override
  Widget build(BuildContext context) {
    return Scaffold(
        appBar: AppBar(
          backgroundColor: Color(0x553CABFF),
          title: Container(
              decoration: BoxDecoration(
                  color: Color(0x432561FF),
                  borderRadius: BorderRadius.circular(15)
              ),
              padding: EdgeInsets.all(10),
              child: Text('Flutter c13')),
          leading: Icon(
              size: 40,
              Icons.access_time_filled,
              color: Color(0xFF402D67)
          ),
          actions: [
            Icon(
                size: 30,
                Icons.search,
                color: Color(0xFF402D67)
            ),
            Icon(
                size: 30,
                Icons.menu,
                color: Color(0xFF402D67)
            )
          ],
        ),
        body: Column(
          children: [
            Row(
              children: [
                HeadLines('الأخبار'),
                SizedBox(
                  width: 10,
                ),
                HeadLines('المجلات')
              ],
            ),
            Expanded(
              child: GridView.builder(
                itemCount: data.length,
                  gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(
                      crossAxisCount: 2,
                  childAspectRatio: 3/2,
                  crossAxisSpacing: 5),
                  itemBuilder: PathWordfung
              ),
            ),
          ],
        )
    );
  }
PathWord PathWordfung(BuildContext context, int position){
    return PathWord(inform:data[position],onTap: (){
      Navigator.of(context)
          .pushNamed(CategoryData.pages,arguments: data[position]);
    }, );
}
  Widget PathWordfun(BuildContext context, int position) {
    CategoryData first = data[position * 2];
    CategoryData? seconed = (position * 2 + 1 < data.length)
        ?data[position*2+1]:null;

    return Row(mainAxisAlignment: MainAxisAlignment.start,
      children: [
        PathWord(inform: first,onTap:(){
          Navigator.of(context)
              .pushNamed(CategoryData.pages,arguments: data[position]);
        }, ),
        SizedBox(width: 5,),
        if(seconed!=null) PathWord(inform: seconed,onTap: (){
          Navigator.of(context)
              .pushNamed(CategoryData.pages,arguments: data[position]);
        },)
      ],
    );

  }
}



